var $rows=$("#modlist tbody tr");var searchFilter=/ +/g;var modFilter=/\s+/g;$("#search").bind("change keyup input",function(){var b=$.trim($(this).val()).replace(searchFilter," ").toLowerCase();if(b===""){$rows.show()}else{var a=RegExp("\\b"+b+"|"+b+"\\b");$rows.show().filter(function(){var c=$(this).text().replace(modFilter," ").toLowerCase();if(~c.indexOf(b)){return !a.test(c)}return true}).hide()}});$(".advanced-search input").bind("change keyup input",function(){var b=$.trim($("#search-name").val()).replace(searchFilter," ").toLowerCase();var c=$.trim($("#search-author").val()).replace(searchFilter," ").toLowerCase();var d=$.trim($("#search-desc").val()).replace(searchFilter," ").toLowerCase();var a=$.trim($("#search-tag").val()).replace(searchFilter," ").toLowerCase();if(b===""&&c===""&&d===""&&a===""){$rows.show()}else{$rows.show().filter(function(){var h=$(this).find("span").first().text().toLowerCase();var f=$(this).find("i").first().text().toLowerCase();var e="";if($(this).find("p").length>2){e=$(this).find("p:nth-child(2)").text().replace(modFilter," ").toLowerCase()}var g=$(this).find("p:last-child").text().toLowerCase();return !~h.indexOf(b)||!~f.indexOf(c)||!~e.indexOf(d)||!~g.indexOf(a)}).hide()}});$("#search-simple").click(function(){if($(".advanced-search").is(":visible")){$(".advanced-search").addClass("hidden-search");$("#search").prop("disabled",false)}});$("#search-advanced").click(function(){if(!$(".advanced-search").is(":visible")){$(".advanced-search").removeClass("hidden-search");$("#search").prop("disabled",true)}});$("#search-reset").click(function(){$("#search").val("");$(".advanced-search input").val("");$rows.show()});